function fadeTitles(){function e(){o>=1?clearInterval(t):(n.style.opacity=o,o+=.1)}const n=document.querySelector(".fadein"),t=window.setInterval(e,200);var o=.1}function selected(){document.querySelectorAll("div.navbar-wagon-right > a").forEach(function(e){!0===window.location.href.includes(e.pathname)&&e.classList.add("selected")})}function runScroll(){const e=document.querySelector(".navbar-wagon");document.body.scrollTop>=240||document.documentElement.scrollTop>=240?e.classList.add("nav-scroll"):e.classList.remove("nav-scroll")}function attachEvents(){window.addEventListener("scroll",runScroll),selected(),fadeTitles()}document.addEventListener("DOMContentLoaded",attachEvents);