function attachEvents(){window.addEventListener("scroll",runScroll)}function runScroll(){const n=document.querySelector(".navbar-wagon");document.querySelectorAll("div.navbar-wagon-right > a");document.body.scrollTop>=240||document.documentElement.scrollTop>=240?n.classList.add("nav-scroll"):n.classList.remove("nav-scroll")}document.addEventListener("DOMContentLoaded",attachEvents);