function contactInfo(){document.querySelector(".personal"),document.querySelector(".where")}function selected(){document.querySelectorAll("div.navbar-wagon-right > a").forEach(function(e){!0===window.location.href.includes(e.pathname)&&e.classList.add("selected")})}function runScroll(){const e=document.querySelector(".navbar-wagon");document.body.scrollTop>=280||document.documentElement.scrollTop>=280?e.classList.add("nav-scroll"):e.classList.remove("nav-scroll")}function menuBar(){const e=document.getElementById("menu"),n=document.getElementById("overlay");"0%"===n.style.width?(n.style.width="100%",e.innerText="X"):(n.style.width="0%",e.innerText="Menu"),e.addEventListener("click",menuBar,!1)}function smoothAnchor(){$(".arrow").click(function(e){e.preventDefault(),$("html, body").animate({scrollTop:$("#tecnicas").offset().top},1e3)})}function attachEvents(){window.addEventListener("scroll",runScroll,!1),selected(),contactInfo(),menuBar(),smoothAnchor()}document.addEventListener("DOMContentLoaded",attachEvents,!1);