function selected(){document.querySelectorAll("div.navbar-wagon-right > a").forEach(function(e){!0===window.location.href.includes(e.pathname)&&e.classList.add("selected")})}function runScroll(){const e=document.querySelector(".navbar-wagon");document.body.scrollTop>=240||document.documentElement.scrollTop>=240?e.classList.add("nav-scroll"):e.classList.remove("nav-scroll")}function attachEvents(){window.addEventListener("scroll",runScroll),selected()}document.addEventListener("DOMContentLoaded",attachEvents);